  --success-gradient: linear-gradient(135deg, hsl(142, 76%, 45%) 0%, hsl(160, 84%, 50%) 100%);
  --warning-gradient: linear-gradient(135deg, hsl(38, 92%, 60%) 0%, hsl(43, 96%, 65%) 100%);
  --danger-gradient: linear-gradient(135deg, hsl(0, 84%, 70%) 0%, hsl(14, 86%, 65%) 100%);
  --primary-gradient: linear-gradient(135deg, hsl(217, 91%, 60%) 0%, hsl(262, 83%, 70%) 100%);
}

@layer base {
