    color: var(--leetcode-hard);
  }

  .bg-leetcode-easy {
    background-color: var(--leetcode-easy);
  }

  .bg-leetcode-medium {
    background-color: var(--leetcode-medium);
  }

  .bg-leetcode-hard {
    background-color: var(--leetcode-hard);
  }

  .bg-leetcode-primary {
    background-color: var(--leetcode-primary);
  }

  .text-leetcode-primary {
    color: var(--leetcode-primary);
  }

  .border-leetcode-primary {
    border-color: var(--leetcode-primary);
  }

  /* Modern gradient utilities */
  .bg-gradient-primary {
    background: var(--primary-gradient);
  }

  .bg-gradient-success {
    background: var(--success-gradient);
  }

  .bg-gradient-warning {
    background: var(--warning-gradient);
  }

  .bg-gradient-danger {
    background: var(--danger-gradient);
  }

  /* Professional Card Styles */
  .modern-card {
    @apply rounded-2xl border-0 shadow-lg backdrop-blur-sm;
    background: rgba(255, 255, 255, 0.7);
    border: 1px solid rgba(255, 255, 255, 0.2);
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .dark .modern-card {
    background: rgba(51, 65, 85, 0.7);
    border: 1px solid rgba(148, 163, 184, 0.2);
  }

  .modern-card:hover {
    @apply shadow-2xl;
    transform: translateY(-2px);
  }

  /* Hover Effects */
  .hover-lift {
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .hover-lift:hover {
    transform: translateY(-4px);
    box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04);
  }

  /* Animations */
  @keyframes fade-in {
    0% {
      opacity: 0;
      transform: translateY(20px);
    }
    100% {
      opacity: 1;
      transform: translateY(0);
    }
  }

  @keyframes slide-up {
    0% {
      opacity: 0;
      transform: translateY(30px);
    }
    100% {
      opacity: 1;
      transform: translateY(0);
    }
  }

  @keyframes slide-in-right {
    0% {
      opacity: 0;
      transform: translateX(30px);
    }
    100% {
      opacity: 1;
      transform: translateX(0);
    }
  }

  @keyframes scale-in {
    0% {
      opacity: 0;
      transform: scale(0.95);
    }
    100% {
      opacity: 1;
      transform: scale(1);
    }
  }

  .animate-fade-in {
    animation: fade-in 0.6s ease-out;
  }

  .animate-slide-up {
    animation: slide-up 0.6s ease-out;
  }

  .animate-slide-in-right {
    animation: slide-in-right 0.6s ease-out;
  }

  .animate-scale-in {
    animation: scale-in 0.4s ease-out;
  }

  /* Glass morphism effect */
  .glass {
    background: rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(10px);
    border: 1px solid rgba(255, 255, 255, 0.2);
  }

  .dark .glass {
    background: rgba(51, 65, 85, 0.1);
    border: 1px solid rgba(148, 163, 184, 0.1);
  }

  /* Professional button gradients */
  .btn-gradient-primary {
    background: linear-gradient(135deg, hsl(221, 83%, 53%) 0%, hsl(262, 83%, 58%) 100%);
    border: none;
    color: white;
    transition: all 0.3s ease;
  }

  .btn-gradient-primary:hover {
    background: linear-gradient(135deg, hsl(221, 83%, 48%) 0%, hsl(262, 83%, 53%) 100%);
    transform: translateY(-1px);
    box-shadow: 0 10px 25px rgba(59, 130, 246, 0.3);
  }

  /* Difficulty Colors */
  .difficulty-easy {
    color: hsl(142, 76%, 36%);
    background-color: hsla(142, 76%, 36%, 0.1);
  }

  .difficulty-medium {
    color: hsl(38, 92%, 50%);
    background-color: hsla(38, 92%, 50%, 0.1);
  }

  .difficulty-hard {
    color: hsl(0, 84%, 60%);
    background-color: hsla(0, 84%, 60%, 0.1);
  }

  /* Status Badge Styles */
  .status-excellent {
    @apply bg-green-50 text-green-700 dark:bg-green-900/30 dark:text-green-300;
  }

  .status-good {
    @apply bg-emerald-50 text-emerald-700 dark:bg-emerald-900/30 dark:text-emerald-300;
  }

  .status-active {
    @apply bg-blue-50 text-blue-700 dark:bg-blue-900/30 dark:text-blue-300;
  }

  .status-underperforming {
    @apply bg-red-50 text-red-700 dark:bg-red-900/30 dark:text-red-300;
  }

  /* Loading animations */
  .loading-shimmer {
    background: linear-gradient(90deg, #f0f0f0 25%, #e0e0e0 50%, #f0f0f0 75%);
    background-size: 200% 100%;
    animation: shimmer 1.5s infinite;
  }

  .dark .loading-shimmer {
    background: linear-gradient(90deg, #374151 25%, #4b5563 50%, #374151 75%);
    background-size: 200% 100%;
  }

  @keyframes shimmer {
    0% {
      background-position: -200% 0;
    }
    100% {
      background-position: 200% 0;
    }
  }

  /* Responsive utilities */
  @media (max-width: 768px) {
    .modern-card {
      @apply rounded-xl;
    }

    .animate-fade-in,
    .animate-slide-up,
    .animate-slide-in-right,
    .animate-scale-in {
      animation: none;
    }
  }

  /* Custom scrollbar */
  ::-webkit-scrollbar {
    width: 8px;
    height: 8px;
  }

  ::-webkit-scrollbar-track {
    background: hsl(210, 40%, 96%);
  }

  .dark ::-webkit-scrollbar-track {
    background: hsl(217, 32%, 17.5%);
  }

  ::-webkit-scrollbar-thumb {
    background: hsl(215, 16%, 46.9%);
    border-radius: 4px;
  }

  .dark ::-webkit-scrollbar-thumb {
    background: hsl(215, 16%, 65%);
  }

  ::-webkit-scrollbar-thumb:hover {
    background: hsl(221, 83%, 53%);
  }
}

@keyframes fadeIn {
  from { opacity: 0; }
  to { opacity: 1; }
}

@keyframes slideUp {
  from { 
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes bounceIn {
  0% {
    opacity: 0;
    transform: scale(0.3);
  }
  50% {
    transform: scale(1.05);
  }
  70% {
    transform: scale(0.9);
  }
  100% {
    opacity: 1;
    transform: scale(1);
  }
}

