} from "recharts";

interface AnalyticsData {
  summaryStats: {
    totalStudents: number;
    improved: number;
    declined: number;
    same: number;
    averageImprovement: number;
  };
  top10Students: any[];
  top15Improvers: any[];
  progressCategories: {
    improved: number;
    declined: number;
    same: number;
  };
