            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div className="h-96 bg-slate-200 rounded-2xl loading-shimmer"></div>
              <div className="h-96 bg-slate-200 rounded-2xl loading-shimmer"></div>
            </div>
            <div className="h-64 bg-slate-200 rounded-2xl loading-shimmer"></div>
          </div>
        </div>
