#!/usr/bin/env node

/**
 * Complete build script for deployment
 * Runs the full build process and sets up files for any deployment platform
 */

import { spawn } from 'child_process';
import { promises as fs } from 'fs';
import { fileURLToPath } from 'url';
import path from 'path';

// Handle different Node.js environments
const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

function runCommand(command, args = []) {
  return new Promise((resolve, reject) => {
    console.log(`Running: ${command} ${args.join(' ')}`);
    const child = spawn(command, args, { stdio: 'inherit', shell: true });

    child.on('close', (code) => {
      if (code === 0) {
        resolve();
      } else {
        reject(new Error(`Command failed with exit code ${code}`));
      }
    });
  });
}

async function main() {
  try {
    console.log('ğŸš€ Starting complete build for deployment...\n');

    // Step 1: Install dependencies
    console.log('ğŸ“¦ Installing dependencies...');
    await runCommand('npm', ['install']);

    // Step 2: Build the application
    console.log('\nğŸ”¨ Building application...');
    await runCommand('npm', ['run', 'build']);

    // Step 3: Set up deployment files
    console.log('\nğŸ“ Setting up deployment files...');
    await runCommand('node', ['deploy-universal.js']);

    console.log('\nâœ… Build complete! Ready for deployment.');
    console.log('\nTo deploy:');
    console.log('- Render: Use "node build-for-deploy.js" as build command');
    console.log('- Manual: Run "npm start" to start production server');

  } catch (error) {
    console.error('\nâŒ Build failed:', error.message);
    process.exit(1);
  }
}

main();

deploy-setup.js.content:
#!/usr/bin/env node

/**
 * Deployment setup script
 * This script ensures the built client files are in the correct location for production serving
 */

import fs from 'fs';
import path from 'path';
import { fileURLToPath } from 'url';

// Handle different Node.js environments
const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

function copyDir(src, dest) {
  if (!fs.existsSync(dest)) {
    fs.mkdirSync(dest, { recursive: true });
  }

  const files = fs.readdirSync(src);

  for (const file of files) {
    const srcPath = path.join(src, file);
    const destPath = path.join(dest, file);

    if (fs.statSync(srcPath).isDirectory()) {
      copyDir(srcPath, destPath);
    } else {
      fs.copyFileSync(srcPath, destPath);
    }
  }
}

function main() {
  const distPublicPath = path.resolve(__dirname, 'dist', 'public');
  const serverPublicPath = path.resolve(__dirname, 'server', 'public');

  // Additional paths for different deployment environments
  const clientDistPath = path.resolve(__dirname, 'client', 'dist');

  console.log('Setting up deployment files...');
  console.log('Current working directory:', process.cwd());
  console.log('Script directory:', __dirname);

  if (!fs.existsSync(distPublicPath)) {
    console.error('Error: Build directory not found at', distPublicPath);
    console.error('Please run "npm run build" first');
    process.exit(1);
  }

  console.log('Copying client build files to server/public...');
  copyDir(distPublicPath, serverPublicPath);

  // Also create client/dist directory and copy files there for deployment platforms that expect it
  console.log('Creating client/dist directory for deployment compatibility...');
  copyDir(distPublicPath, clientDistPath);

  console.log('Deployment setup complete!');
  console.log('Production server can find static files at:', serverPublicPath);
  console.log('Deployment platforms can find files at:', clientDistPath);
}

main();

deploy-universal.js.content:
#!/usr/bin/env node

/**
 * Universal deployment setup script
 * Handles deployment for various platforms by creating build files in multiple expected locations
 */

import fs from 'fs';
import path from 'path';
import { fileURLToPath } from 'url';

// Handle different Node.js environments
const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

function copyDir(src, dest) {
  if (!fs.existsSync(dest)) {
    fs.mkdirSync(dest, { recursive: true });
  }

  const files = fs.readdirSync(src);

  for (const file of files) {
    const srcPath = path.join(src, file);
    const destPath = path.join(dest, file);

    if (fs.statSync(srcPath).isDirectory()) {
      copyDir(srcPath, destPath);
    } else {
      fs.copyFileSync(srcPath, destPath);
    }
  }
}

function createSymlinkSafe(target, linkPath) {
  try {
    if (fs.existsSync(linkPath)) {
      const stats = fs.lstatSync(linkPath);
      if (stats.isSymbolicLink()) {
        fs.unlinkSync(linkPath);
      } else if (stats.isDirectory()) {
        fs.rmSync(linkPath, { recursive: true });
      }
    }

    const linkDir = path.dirname(linkPath);
    if (!fs.existsSync(linkDir)) {
      fs.mkdirSync(linkDir, { recursive: true });
    }

    fs.symlinkSync(target, linkPath, 'dir');
    console.log(`âœ… Created symlink: ${linkPath} -> ${target}`);
    return true;
  } catch (error) {
    console.log(`âš ï¸  Symlink failed, will use copy: ${error.message}`);
    return false;
  }
}

function main() {
  const distPublicPath = path.resolve(__dirname, 'dist', 'public');

  console.log('ğŸš€ Universal deployment setup starting...');
  console.log('Current working directory:', process.cwd());
  console.log('Script directory:', __dirname);

  if (!fs.existsSync(distPublicPath)) {
    console.error('âŒ Build directory not found at', distPublicPath);
    console.error('Please run "npm run build" first');
    process.exit(1);
  }

  // List of all possible locations where deployment platforms might look
  const deploymentPaths = [
    path.resolve(__dirname, 'server', 'public'),           // Our server expects this
    path.resolve(__dirname, 'client', 'dist'),             // Some platforms expect this
    path.resolve(__dirname, 'public'),                     // Root public directory
    path.resolve(__dirname, 'build'),                      // Alternative build directory
  ];

  console.log('ğŸ“ Creating build files in all expected locations:');

  for (const targetPath of deploymentPaths) {
    console.log(`\nğŸ“‚ Setting up: ${targetPath}`);

    // Try to create symlink first (faster and uses less space)
    const symlinkSuccess = createSymlinkSafe(distPublicPath, targetPath);

    if (!symlinkSuccess) {
      // Fall back to copying files
      console.log(`ğŸ“‹ Copying files to: ${targetPath}`);
      copyDir(distPublicPath, targetPath);
      console.log(`âœ… Files copied to: ${targetPath}`);
    }
  }

  // Verify all paths exist
  console.log('\nğŸ” Verification:');
  let allGood = true;

  for (const targetPath of deploymentPaths) {
    const exists = fs.existsSync(targetPath);
    const hasIndex = exists && fs.existsSync(path.join(targetPath, 'index.html'));
    console.log(`${hasIndex ? 'âœ…' : 'âŒ'} ${targetPath} ${hasIndex ? '(has index.html)' : '(missing or no index.html)'}`);
    if (!hasIndex) allGood = false;
  }

  if (allGood) {
    console.log('\nğŸ‰ Universal deployment setup complete!');
    console.log('Production server should work on any deployment platform.');
  } else {
    console.log('\nâš ï¸  Some paths were not set up correctly. Check the errors above.');
  }
}

main();

